import { Component, OnInit } from '@angular/core';
import { FundamentalSummary } from '../_services/Models/fundamentalSummary';
import { WatchListService } from '../_services/WatchListService';

@Component({
  selector: 'app-watchlist',
  templateUrl: './watchlist.component.html',
  styleUrls: ['./watchlist.component.css']
})
export class WatchlistComponent {

  watchListdata: FundamentalSummary[] = [];
  model: any = {};

  constructor(private client: WatchListService) { }

  ngOnInit(): void {
    //fill the wathclist
    this.client.getWatchlistData().subscribe({
      next: (data) => {
        this.watchListdata = data;
        console.log(this.watchListdata);
      },
      error: (error) => console.log("There was an error in receiving the data:" + error)
    });
  }

  //average function
  average(data: FundamentalSummary[], variable: keyof FundamentalSummary): number {
    if (data.length === 0) {
      throw new Error("Data array is empty");
    }

    const sum = data.reduce((total, item) => total + (+item[variable]), 0);
    const average = sum / data.length;

    return average;
  }
}
<div class="my-3 text-left">
    <form class="form-inline">
        <input name="ticker" class="form-control" placeholder="Ticker to add" [(ngModel)="model.ticker"]/>
        <button class="btn-primary ml-3" type="submit"></button>
    </form>
</div>

<table class="table">
    <thead class="thead-dark text-center">
        <tr>
            <th scope="col">#</th>
            <th scope="col">Ticker</th>
            <th scope="col">Name</th>
            <th scope="col">Dividend Yield</th>
            <th scope="col">Payout Ratio</th>
            <th scope="col">Net Profit Margin</th>
            <th scope="col">Pe Forward</th>
            <th scope="col">Actions</th>
        </tr>
    </thead>
    <tbody class="text-center">
        <tr *ngFor="let item of watchListdata; let i = index">
            <th scope="row">{{i + 1}}</th>
            <td>{{item.ticker}}</td>
            <td>{{item.name}}</td>
            <td>{{item.dividendYield * 100 | number:'1.2-2'}}%</td>
            <td>{{item.payoutRatio * 100 | number:'1.2-2'}}%</td>
            <td>{{item.profitMargin * 100 | number:'1.2-2'}}%</td>
            <td>{{item.peForward}}</td>
            <td><app-remove></app-remove></td>
        </tr>
        <tr class="table-primary font-weight-bold">
            <th scope="row"></th>
            <td></td>
            <td></td>
            <td>{{average(watchListdata, "dividendYield") * 100 | number:'1.2-2'}}%</td>
            <td>{{average(watchListdata, "payoutRatio") * 100 | number:'1.2-2'}}%</td>
            <td>{{average(watchListdata, "profitMargin") * 100 | number:'1.2-2'}}%</td>
            <td>{{average(watchListdata, "peForward") | number:'1.2-2'}}</td>
            <td></td>
        </tr>
    </tbody>
</table>
