import { Component, EventEmitter, Output } from '@angular/core';
import { SymbolService } from '../../_services/symbol.service';

@Component({
  selector: 'app-ticker-search',
  templateUrl: './ticker-search.component.html',
  styleUrls: ['./ticker-search.component.css']
})
export class TickerSearchComponent {

  @Output()
  addTickerSymbol = new EventEmitter();

  searchTerm: string = "";
  filteredData: string[] = [];

  constructor(private symbolService: SymbolService) { }

  sendTicker() {
    if (this.searchTerm != "") {
      this.addTickerSymbol.emit(this.searchTerm);
    }
  }

  tickerClick(clickedTicker: string) {
    this.searchTerm = clickedTicker;
    this.filteredData = [];
  }

  filterResults() {
    if (this.searchTerm.length > 0) {
      this.symbolService.searchTickers(this.searchTerm).subscribe({
        next: (data: string[]) => {
          this.filteredData = data;
        },
        error: (error) => console.log("There was an error in searching the symbol vy ticker:" + error.message)
      });
    }
    else {
      this.filteredData = [];
    }

  }

}
<div>
    <div class="my-3 text-left">
        <form #searchTickerForm="ngForm" class="form-inline" (ngSubmit)="sendTicker()" autocomplete="off">
            <input name="ticker" class="form-control" placeholder="Enter ticker..." [(ngModel)]="searchTerm" (input)="filterResults()" />
            <button class="btn btn-primary ml-3" type="submit">Add</button>
        </form>
    </div>
    <div class="position-absolute bg-white border" *ngIf="filteredData.length > 0" id="results">
        <p (click)="tickerClick(item)" class="mx-3 btn-light" style="cursor:pointer" *ngFor="let item of filteredData">{{ item }}</p>
    </div>
</div>
