import { Component, OnInit } from '@angular/core';
import { FundamentalSummary } from '../_services/Models/fundamentalSummary';
import { WatchListService } from '../_services/watchListService';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-watchlist',
  templateUrl: './watchlist.component.html',
  styleUrls: ['./watchlist.component.css']
})
export class WatchlistComponent {

  watchListdata: FundamentalSummary[] = [];
  model: any = {};

  constructor(private watchListService: WatchListService, private toast: ToastrService) { }

  ngOnInit(): void {
    //fill the wathclist
    this.getWatchlistData();
  }

  addToWatchList() {
    this.watchListService.addToWatchList(this.model.ticker).subscribe({
      next: (data) => {
        this.getWatchlistData();
        this.toast.success('Symbol added to the watchlist');
      },
      error: (error) => console.log("There was an error in adding the symbol:" + error.message)
    });
  }

  addToWatchListFrom(ticker: string) {
    this.watchListService.addToWatchList(ticker).subscribe({
      next: (data) => {
        this.getWatchlistData();
        this.toast.success('Symbol added to the watchlist');
      },
      error: (error) => console.log("There was an error in adding the symbol:" + error.message)
    });
  }

  removeFromWatchList() {
    this.getWatchlistData();
    this.toast.success('Symbol removed from watchlist');
  }

  getWatchlistData() {
    this.watchListService.getWatchlistData().subscribe({
      next: (data) => {
        this.watchListdata = data;
      },
      error: (error) => console.log("There was an error in receiving the data:" + error.message)
    });
  }

  //average function
  average(data: FundamentalSummary[], variable: keyof FundamentalSummary): number {
    if (data.length === 0) {
      /*throw new Error("Data array is empty");*/
      return 0;
    }

    const sum = data.reduce((total, item) => total + (+item[variable]), 0);
    const average = sum / data.length;

    return average;
  }
}
<app-nav></app-nav>

<div class="container mt-5">
    <div class="row">
        <div class="my-3 text-left col">
            <form #addToWatchListForm="ngForm" class="form-inline" (ngSubmit)="addToWatchList()" autocomplete="off">
                <input name="ticker" class="form-control" placeholder="Ticker to add" [(ngModel)]="model.ticker" />
                <button class="btn btn-primary ml-3" type="submit">Add</button>
            </form>
        </div>
        <div class="my-3 text-right col">
            <button class="btn btn-info" (click)="getWatchlistData()">Update Watchlist</button>
        </div>
    </div>
    <div class="row ml-2">
        <app-ticker-search (addTickerSymbol)="addToWatchListFrom($event)"></app-ticker-search>
    </div>


    <table class="table">
        <thead class="thead-dark text-center">
            <tr>
                <th scope="col">#</th>
                <th scope="col">Ticker</th>
                <th scope="col">Name</th>
                <th scope="col">Dividend Yield</th>
                <th scope="col">Payout Ratio</th>
                <th scope="col">Net Profit Margin</th>
                <th scope="col">Pe Forward</th>
                <th scope="col">Actions</th>
            </tr>
        </thead>
        <tbody class="text-center">
            <tr *ngFor="let item of watchListdata; let i = index">
                <th scope="row">{{i + 1}}</th>
                <td>{{item.ticker}}</td>
                <td>{{item.name}}</td>
                <td>{{item.dividendYield * 100 | number:'1.2-2'}}%</td>
                <td>{{item.payoutRatio * 100 | number:'1.2-2'}}%</td>
                <td>{{item.profitMargin * 100 | number:'1.2-2'}}%</td>
                <td>{{item.peForward}}</td>
                <td><app-remove [ticker]="item.ticker" (updateWatchList)="removeFromWatchList()"></app-remove></td>
            </tr>
            <tr class="table-primary font-weight-bold">
                <th scope="row"></th>
                <td></td>
                <td></td>
                <td>{{average(watchListdata, "dividendYield") * 100 | number:'1.2-2'}}%</td>
                <td>{{average(watchListdata, "payoutRatio") * 100 | number:'1.2-2'}}%</td>
                <td>{{average(watchListdata, "profitMargin") * 100 | number:'1.2-2'}}%</td>
                <td>{{average(watchListdata, "peForward") | number:'1.2-2'}}</td>
                <td></td>
            </tr>
        </tbody>
    </table>
</div>
